tinymce.PluginManager.add("codeinline", function (editor, url) {
    var openDialog = function () {
        return editor.windowManager.open({
            title: "插入行内代码",
            body: {
                type: "panel",
                items: [
                    {
                        type: "input",
                        name: "codeinline",
                    },
                ],
            },
            buttons: [
                {
                    type: "cancel",
                    text: "取消",
                },
                {
                    type: "submit",
                    text: "插入",
                    primary: true,
                },
            ],
            onSubmit: function (api) {
                var data = api.getData();

                // 包裹内容
                let prefix = '<code>'
                let suffix = '</code>'

                // 将输入框内容插入到内容区光标位置
                editor.insertContent(`${prefix}${data.codeinline}${suffix}`);
                api.close();
            },
        });
    };

    // 注册一个工具栏按钮名称
    editor.ui.registry.addButton("codeinline", {
        icon : 'codeinline',
        tooltip: "插入行内代码",
        onAction: function () {
            openDialog();
        },
    });

    return {
        getMetadata: function () {
            return {
                //插件名和链接会显示在“帮助”→“插件”→“已安装的插件”中
                name: "codeinline", //插件名称
                url: "https://github.com/fuyanlove", //作者网址
            };
        },
    };
});
