tinymce.PluginManager.add("latex", function (editor, url) {
    var openDialog = function () {
        return editor.windowManager.open({
            title: "数学公式",
            size: 'medium',
            body: {
                type: "panel",
                items: [
                    {
                        type: "textarea",
                        name: "latex",
                        label: `<a style="margin-bottom: 10px;display: block;color: #0366D6;font-weight: bold;" href="/tool/16295" target="_blank">💙 查看帮助文档</a>`,
                    },
                ],
            },
            buttons: [
                {
                    type: "cancel",
                    text: "取消",
                },
                {
                    type: "submit",
                    text: "插入",
                    primary: true,
                },
            ],
            onSubmit: function (api) {
                var data = api.getData();

                // 包裹内容
                let prefix = '<pre class="e-latex-area w-latex">'
                let suffix = '</pre>'

                // 将输入框内容插入到内容区光标位置
                editor.insertContent(`${prefix}${data.latex}${suffix}`);
                api.close();
            },
        });
    };

    // 注册一个工具栏按钮名称
    editor.ui.registry.addButton("latex", {
        // icon : 'latex',
        text: "Σ",
        tooltip: "插入Latex",
        onAction: function () {
            openDialog();
        },
    });

    // 注册一个菜单项名称 menu/menubar
    // editor.ui.registry.addMenuItem("latex", {
    //     text: "Example菜单名",
    //     onAction: function () {
    //         openDialog();
    //     },
    // });

    return {
        getMetadata: function () {
            return {
                //插件名和链接会显示在“帮助”→“插件”→“已安装的插件”中
                name: "latex", //插件名称
                url: "https://github.com/fuyanlove", //作者网址
            };
        },
    };
});
