tinymce.PluginManager.add("printpage", function (editor, url) {
    var insertPrintpage = function () {
        let html = `<img class="e-print-pagebreak" src="data:image/gif;base64,R0lGODlhAQABAIAAAAAAAP///yH5BAEAAAAALAAAAAABAAEAAAIBRAA7" />`;
        editor.insertContent(html);
    };

    // 注册一个工具栏按钮名称
    editor.ui.registry.addButton("printpage", {
        icon: "print",
        tooltip: "插入打印分页符",
        onAction: function () {
            insertPrintpage();
        },
    });

    return {
        getMetadata: function () {
            return {
                //插件名和链接会显示在“帮助”→“插件”→“已安装的插件”中
                name: "printpage", //插件名称
                url: "https://github.com/fuyanlove", //作者网址
            };
        },
    };
});
